var tabs=[];
node.warn('p='+msg.payload.tabid);
node.warn('px='+msg.tabid);
for(var iF in msg.payload.flow) {
    var flow=msg.payload.flow[iF];
    if(flow.type == "tab") {
        flow.nodes=[];
        tabs.push(flow);
    } else if(flow.z!==null) {
        // this is part of tab
        for(var iT in tabs) {
            var tab=tabs[iT];
            if(tab.id==flow.z) {
                tab.nodes.push(flow);
            }
        }
    }
}
msg.tabs=tabs;
return msg;

var tabid=msg.tabid;
var tab;

for(var iF in msg.payload.flow) {
    var flow=msg.payload.flow[iF];
    if(flow.type == "tab" && flow.id==tabid) {
        flow.nodes=[];
        tab=flow;
    } else if(flow.z!==null && flow.z==tabid) {
        // this is part of tab
        if(tab.id==flow.z) {
            tab.nodes.push(flow);
        }
    }
}

function replacer(key, value) {
    // round x, y and drop z
    if (key === "z") {
        return undefined;
    } else if (typeof value === "number") {
        return Math.round(value);
    }

    return value;
}

msg.payload=JSON.stringify(tab.nodes,replacer);
var attch='attachment; filename='+encodeURIComponent(tab.label)+'.json';
msg.headers={// 'content-length': '123',
    'content-type': 'application/octet-stream',
    'content-disposition': attch};
return msg;
